class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0;
        int n = grid.size(), m = grid[0].size();
        int j = 0, i = 0;

        for (int j = 0; j < m / 2; j++) {
            for (int i = 0; i < n / 2; i++) {
                int one = 0;
                if (grid[i][j] == 1)
                    one++;
                if (grid[n - i - 1][j] == 1)
                    one++;
                if (grid[i][m - 1 - j] == 1)
                    one++;
                if (grid[n - i - 1][m - 1 - j])
                    one++;
                ans += min(one, 4 - one);
            }
        }

        if (m % 2 == 1) {
            int count_one = 0;
            int flip = 0;
            for (int i = 0; i < n / 2; i++) {
                if (grid[i][m / 2] != grid[n - i - 1][m / 2]) {
                    ans++;
                    flip++;
                } else if (grid[i][m / 2] == grid[n - i - 1][m / 2] &&
                           grid[i][m / 2] == 1)
                    count_one++;
            }
            if (n & 1) {
                for (int j = 0; j < m / 2; j++) {
                    if (grid[n / 2][j] != grid[n / 2][m - j - 1]) {
                        ans++;
                        flip++;
                    } else if (grid[n / 2][j] == grid[n / 2][m - j - 1] &&
                               grid[n / 2][j] == 1)
                        count_one++;
                }
            }
            if (count_one % 2 == 1 && flip == 0)
                ans += 2;
            if (n % 2 == 1 && grid[n / 2][m / 2] == 1)
                ans++;
        } else if (n % 2 == 1) {
            int count_one = 0;
            int flip = 0;
            for (int j = 0; j < m / 2; j++) {
                if (grid[n / 2][j] != grid[n / 2][m - j - 1]) {
                    ans++;
                    flip++;
                } else if (grid[n / 2][j] == grid[n / 2][m - j - 1] &&
                           grid[n / 2][j] == 1)
                    count_one++;
            }
            if (count_one % 2 == 1 && flip == 0)
                ans += 2;
        }
        return ans;
    }
};
