class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0;

        int n = grid.size();
        int m = grid[0].size();

        for(int i= 0; i<n/2; i++){
            for(int j = 0; j<m/2; j++){

                int cnt = grid[i][j]+ grid[i][m-j-1]+ grid[n-i-1][j]+ grid[n-i-1][m-j-1];

                ans += min(cnt, 4- cnt);
            }
        }
        // return ans;

        int c1 = 0, c2 = 0;
        int c3 = 0, c4 = 0;

        if(n%2){
            for(int j = 0; j<m/2;j++){
                c3 += (grid[n/2][j]!= grid[n/2][m-j-1]);
                if(grid[n/2][j]== grid[n/2][m-j-1] && grid[n/2][j]== 1){
                    c1 += 2;
                }
            }
        }
        if(m%2){
            for(int i=0;i< n/2; i++){
                c4 += (grid[i][m/2]!= grid[n-i-1][m/2]);
                if((grid[i][m/2]== grid[n-i-1][m/2]) && grid[i][m/2]== 1){
                    c2 += 2;
                }
            }
        }

        int c5 = 0;
        if(n%2 && m%2){
            c5 = grid[n/2][m/2];
        }

        if(n%2== 0 && m%2== 0){
            return ans;
        }
        ans += c5;

        if((c1+ c2)%4== 0){
            ans += c3+ c4;
        }
        else{
            if(c3|| c4){
                ans += c3+ c4;
            }
            else{
                ans += 2;
            }
        }

        return ans;










    }
};
