class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n= grid.size(), m = grid[0].size();
        int as=0,cnt=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int z=0,o=0;
                if(grid[i][j]==0)z++;
                else o++;
                if(grid[n-1-i][j]==0)z++;
                else o++;
                if(grid[i][m-1-j]==0)z++;
                else o++;
                if(grid[n-1-i][m-1-j]==0)z++;
                else o++;
                if(z==o)as+=2;
                else if(z==1 || o==1)as++;
            }
        }
        int t1=0, t2=0;
        if(m&1){
            int j = m/2;
            //int t1=0;
            for(int i=0;i<n/2;i++){
                if(grid[i][j]!=grid[n-1-i][j])t1++;
                else{
                    if(grid[i][j]==1)cnt+=2;
                }
            }
        }
        if(n&1){
            int i= n/2;
            //int t2=0;
            for(int j=0;j<m/2;j++){
                if(grid[i][j]!=grid[i][m-1-j])t2++;
                else{
                    if(grid[i][j]==1)cnt+=2;
                }
            }
        }
        if(m&1 && n&1 && grid[n/2][m/2]==1)as++;
        if(cnt%4==0 || t1>0 || t2>0){
            return as + t1 + t2;
        }
        else{
            return as + 2;
        }
    }
};
