class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size(),n=grid[0].size();
        int res=0,x=0,y=0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int k=grid[i][j]+grid[i][n-j-1]+grid[m-i-1][j]+grid[m-1-i][n-1-j];
                res+=min(k,4-k);
            }
        }
        if(m&1)for(int j=0;j<n/2;j++){
            if(grid[m/2][j]==grid[m/2][n-j-1]){
                if(grid[m/2][j])x+=2;
                continue;
            }
            res++;
            y++;
        }
        if(n&1)for(int i=0;i<m/2;i++){
            if(grid[i][n/2]==grid[m-1-i][n/2]){
                if(grid[i][n/2])x+=2;
                continue;
            }
            res++;
            y++;
        }
        if(m&1&&n&1&&grid[m/2][n/2])res++;
        x=(x+y)%4;
        if(x)res+=max(min(x,4-x)-y,0);
        return res;
    }
};
