class Solution {
public:
    int minFlips(vector<vector<int>>& v) {
         int n=v.size(); int m=v[0].size(); int ans=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int cnt1=0; int cnt0=0;
                  if(v[i][j]==1) cnt1++;
                  else cnt0++;
                  if(v[i][m-1-j]==1) cnt1++;
                  else cnt0++;
                  if(v[n-1-i][j]==1) cnt1++;
                  else cnt0++;
                  if(v[n-1-i][m-1-j]==1) cnt1++;
                  else cnt0++;  
                ans+=min(cnt1,cnt0);
            }
        }
        cout<<ans<<endl;
          if(n%2==0 && m%2==0) return ans;
          else if(n%2==0){
              int cnt=0; int no=0;
              for(int i=0;i<n/2;i++){
                  if(v[i][m/2]==v[n-1-i][m/2] && v[i][m/2]==1)
                     cnt++;
                  else if(v[i][m/2]!=v[n-1-i][m/2])
                      no++;
              }
              if(cnt%2==0) return ans+no;
              else{
                  if(no==0 && cnt!=0)
                      return ans+2;
                  else
                      return ans+no;
              }
          }
          else if(m%2==0){
               int cnt=0; int no=0;
              for(int i=0;i<m/2;i++){
                  if(v[n/2][i]==v[n/2][m-1-i] && v[n/2][i]==1)
                     cnt++;
                  else if(v[n/2][i]!=v[n/2][m-1-i])
                      no++;
              }
              if(cnt%2==0) return ans+no;
              else{
                  if(no==0 && cnt!=0)
                      return ans+2;
                  else
                      return ans+no;
              }
          }
        else{
              int cnt=0; int no=0;
              for(int i=0;i<n/2;i++){
                  if(v[i][m/2]==v[n-1-i][m/2] && v[i][m/2]==1)
                     cnt++;
                  else if(v[i][m/2]!=v[n-1-i][m/2])
                      no++;
              }
              for(int i=0;i<m/2;i++){
                  if(v[n/2][i]==v[n/2][m-1-i] && v[n/2][i]==1)
                     cnt++;
                  else if(v[n/2][i]!=v[n/2][m-1-i])
