class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size(), n=grid[0].size();
        int ans=0;
        int count1=0;
        for(int i=0; i<m/2; i++)
        {
            for(int j=i; j<n/2; j++)
            {
                // cout<<i<<", "<<j<<endl;
                int ones=grid[i][j]+grid[i][n-1-j]+grid[m-1-i][j]+grid[m-1-i][n-1-j];
                // cout<<grid[i][j]<<" "<<grid[i][n-1-j]<<" "<<grid[m-1-i][j]<<" "<<grid[m-1-i][n-1-j]<<endl;
                count1+=min(ones, 4-ones);
            }
        }
        for(int j=0; j<n/2; j++)
        {
            for(int i=j+1; i<m/2; i++)
            {
                // cout<<i<<", "<<j<<endl;
                int ones=grid[i][j]+grid[i][n-1-j]+grid[m-1-i][j]+grid[m-1-i][n-1-j];
                count1+=min(ones, 4-ones);
            }
        }
        int r=0;
        int ones=0;
        if(m&1)
        {
            for(int j=0; j<n/2; j++)
            {
                int val=grid[m/2][j]+grid[m/2][n-1-j];
                if(val==1) ++r;
                if(val==2) ones+=2;
            }
        }
        int c=0;
        if(n&1)
        {
            for(int i=0; i<m/2; i++)
            {
                int val=grid[i][n/2]+grid[m-1-i][n/2];
                if(val==1) ++c;
                if(val==2) ones+=2;
            }
        }
        if((m&1) && (n&1)) ones+=grid[m/2][n/2];

        // cout<<ones<<" - "<<count1<<" "<<r<<" "<<c<<endl;

        int flips = count1+r+c;
        int rem=ones%4;
        if(rem&1)
        {
            if((m&1) && (n&1))
            {
                if(grid[m/2][n/2]) rem-=1, flips++;
            }
        }
        if(rem==2)
        {
            if(r>0 || c>0)
            {}
            else flips+=2;
        }

        return flips;
    }
};
