class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans=0;
        for(int i=0;i<=n/2;i++){
          for(int j=0;j<=m/2;j++){
            if(j==m-j-1 && i==n-i-1){
                if(grid[i][j]==1){
                    grid[i][j]=0;
                    ans++;
                }
            }
            else if(j==m-j-1 || i==n-i-1){
                // int oneCount = grid[i][j] + grid[n-i-1][j] + grid[i][m-j-1] + grid[n-i-1][m-j-1];
                // oneCount/=2;

                // grid[i][j]= grid[n-i-1][j]= grid[i][m-j-1]= grid[n-i-1][m-j-1]=0;
                // // if(oneCount<=1){
                // // }else{
                // // grid[i][j]= grid[n-i-1][j]= grid[i][m-j-1]= grid[n-i-1][m-j-1]=1;
                // // }
                // ans+=oneCount;
            }
            else{
                int oneCount = grid[i][j] + grid[n-i-1][j] + grid[i][m-j-1] + grid[n-i-1][m-j-1];
                if(oneCount<=2){
                grid[i][j]= grid[n-i-1][j]= grid[i][m-j-1]= grid[n-i-1][m-j-1]=0;
                }else{
                grid[i][j]= grid[n-i-1][j]= grid[i][m-j-1]= grid[n-i-1][m-j-1]=1;
                }
                ans+=min(oneCount,4-oneCount);
            }
            // cout<<i<<" "<<j<<" "<<ans<<endl;
          }
        }
        int flips = 0,ones =0;
        if(n%2==1){
            for(int j=0;j<m/2;j++){
                if(grid[n/2][j]!=grid[n/2][m-j-1]){
                    flips++;
                }
                else if(grid[n/2][j]==1){
                    ones++;
                }
            }
        }
        if(m%2==1){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-i-1][m/2]){
                    flips++;
                }
                else if(grid[i][m/2]==1){
                    ones++;
                }
            }
        }
        ans+=flips;
        if(ones%2==1 && flips==0){
            ans+=2;
        }
        // for(int i=0;i<n;i++){
        //   for(int j=0;j<m;j++){
        //     cout<<grid[i][j]<<" ";
        //   }cout<<endl;
        // }
        return ans;
    }
};
