class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int extra=0,ans=0;
        int ones=0,e1=0,e2=0,e3=0;
        for(int i=0;i<((n+1)/2);i++){
            for(int j=0;j<((m+1)/2);j++){
                int i1=i,j1=j;
                int i2=i,j2=m-j-1;
                int i3=n-i-1,j3=j;
                int i4=n-i-1,j4=m-j-1;
                if(i1!=i3&&j2!=j1){
                    int op=grid[i1][j1]+grid[i2][j2]+grid[i3][j3]+grid[i4][j4];
                    ones+=op;
                    if(op==1){
                        ones--;
                        ans++;
                    }
                    else if(op==3){
                        ones++;
                        ans++;
                    }
                    else if(op==2){
                        ones+=2;
                        ans+=2;
                    }
                }
                else if(i1!=i3){
                    int op=grid[i1][j1]+grid[i3][j3];
                    ones+=op;
                    if(op==1){
                        extra++;
                        ans++;
                        ones--;
                    }
                    else{
                        e1++;
                    }
                }
                else if(j2!=j1){
                    int op=grid[i1][j1]+grid[i2][j2];
                    ones+=op;
                    if(op==1){
                        extra++;
                        ans++;
                        ones--;
                    }
                    else{
                        e1++;
                    }
                }
                else{
                    int op=grid[i][j];
                    ones+=op;
                    if(op==0)
                    e2=1;
                    else
                    e3=1;
                }
            }
        }
        if(ones%4==0){
            return ans;
        }
        else if(ones%4==1){
            if(e3==1)
            return ans+1;
            if(extra>=1)
            return ans+1;
            return ans+3;
        }
        else if(ones%4==2){
            if(extra>=1)
            return ans;
            return ans+2;
        }
        else{
            if(e2==1)
            return ans+1;
            if(extra>=1)
            return ans+1;
            return ans+3;
        }
    }
};
