class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size(),n=grid[0].size();
        int ans=0;
        int c=0;
        bool f1=false;
        for(int i=0;i<=m-1-i;i++)
            for(int j=0;j<=n-1-j;j++){
                vector<int> mm(2,0);
                set<pair<int,int>> ss;
                ss.insert({i,j});
                ss.insert({i,n-1-j});
                ss.insert({m-1-i,j});
                ss.insert({m-1-i,n-1-j});
                int d=0;
                for(auto [x,y]:ss){
                    mm[grid[x][y]]++;
                    d=max(d,mm[grid[x][y]]);
                }
                ans+=ss.size()-d;
                if(ss.size()==4){
                }else if(ss.size()==2){
                    if(mm[1]==1){
                        f1=true;
                    }else c+=mm[1];
                }else{
                    if(mm[1]==1) ans++;
                }
            }
        if(c%4==2){
            return f1?ans:ans+2;
        }else
            return ans;
    }
};
