class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m = len(grid)
        n = len(grid[0])
        answer = 0
        for i in range(m // 2):
            for j in range(n // 2):
                count = 1
                temp = grid[i][j]
                if grid[i][-j - 1] == temp:
                    count += 1
                if grid[-i-1][j] == temp:
                    count += 1
                if grid[-i-1][-j-1] == temp:
                    count += 1
                answer += min (count, 4 - count)
        count = 0
        ones = 0
        #print(answer)
        if m % 2 == 1:
            for j in range(n//2):
                temp = grid[m // 2][j]
                ones += 2 * temp
                if grid[m//2][-j-1] != temp:
                    count += 1
        if n % 2 == 1:
            for i in range(m//2):
                temp = grid[i][n//2]
                ones += 2 * temp
                if grid[-i-1][n//2] != temp:
                    count += 1
        if ones != 0 and ones %4 != 0 and count == 0:
            answer += 2
        else:
            answer += count
        #print(answer)
        if m % 2 == 1 and n % 2 == 1:
            answer += grid[m//2][n//2]
        return answer
