class Solution {
public:
    int minFlips(vector<vector<int>>& arr) {
        int ans=0;
        int n=arr.size();
        int m=arr[0].size();
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int p=(arr[i][j]==1)+(arr[i][m-j-1]==1)+(arr[n-i-1][j]==1)+(arr[n-i-1][m-j-1]==1);
                ans+=min(4-p,p);
            }
        }
        int k=0,l=0;
        if(n%2){
            for(int i=0;i<m/2;i++){
                if(arr[(n)/2][i]!=arr[(n)/2][m-1-i])k++;
                if(arr[(n)/2][i]==arr[(n)/2][m-1-i] && arr[(n)/2][i]==1)l+=2;
            }
        }
        if(m%2){
            for(int i=0;i<n/2;i++){
                if(arr[i][(m)/2]!=arr[n-1-i][(m)/2])k++;
                if(arr[i][(m)/2]==arr[n-1-i][(m)/2] && arr[i][(m)/2]==1)l+=2;
            }
        }
        if(n%2 && m%2)ans+=arr[(n)/2][(m)/2];
        // cout<<ans<<endl;
        if(k>0 || l%4==0){
        cout<<ans+k<<endl;
            return ans+k;}
        else {
            cout<<ans+2<<endl;
            return ans+2;
        }
    }
};
