class Solution {
    public int minFlips(int[][] grid) {
        int ans = 0;
        int singleOnes = 0;
        int singleZeros = 0;
        int doubleOneOne = 0;
        int doubleOneZero = 0;
        int doubleZeroZero = 0;

        for(int i = 0; i < grid.length; i++){
            for(int j = 0; j < grid[0].length; j++){
                if(grid[i][j] == -1) continue;
                int ii = grid.length - 1 - i;
                int jj = grid[0].length -1 - j;

                if(i == ii && j == jj){
                    if(grid[i][j] == 1) singleOnes++;
                    else singleZeros++;
                    grid[i][j] = -1;
                }
                else if(i == ii && j != jj){
                    int ones = 0;
                    if(grid[i][j] == 1) ones++;
                    if(grid[i][jj] == 1) ones++;

                    grid[i][j] = -1;
                    grid[i][jj] = -1;

                    if(ones == 0) doubleZeroZero++;
                    else if(ones == 1) doubleOneZero++;
                    else doubleOneOne++;
                }
                else if(i != ii && j == jj){
                    int ones = 0;
                    if(grid[i][j] == 1) ones++;
                    if(grid[ii][j] == 1) ones++;

                    grid[i][j] = -1;
                    grid[ii][j] = -1;

                    if(ones == 0) doubleZeroZero++;
                    else if(ones == 1) doubleOneZero++;
                    else doubleOneOne++;
                }
                else{
                    int ones = 0;
                    if(grid[i][j] == 1) ones++;
                    if(grid[i][jj] == 1) ones++;
                    if(grid[ii][j] == 1) ones++;
                    if(grid[ii][jj] == 1) ones++;
                    if(ones == 1 || ones == 3) ans++;
                    else if(ones == 2) ans += 2;

                    grid[i][j] = -1;
                    grid[ii][j] = -1;
                    grid[i][jj] = -1;
                    grid[ii][jj] = -1;
                }
            }
        }
        System.out.println("four: " + ans);

        ans += doubleOneZero;

        System.out.println("++10: " + ans);
        int ones = singleOnes + doubleOneOne * 2;
        System.out.println("ones = " + ones);
        if(ones % 4 == 0) return ans;
        if(ones % 4 == 1) return ans + 1;
        if(ones % 4 == 2) {
            if(doubleOneZero > 0) return ans;
            return ans + 2;
        }
        if(ones % 4 == 3 && singleZeros > 0) return ans + 1;
        else if(ones % 4 == 3 && doubleOneZero > 0) return ans + 1;
        return ans + 3;
    }
}
