class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;
        for(int i = 0; i < n/2; i++){
            for(int j = 0; j < m/2; j++){
                int cnt[2]{};
                cnt[grid[i][j]]++;
                cnt[grid[i][m-j-1]]++;
                cnt[grid[n-i-1][j]]++;
                cnt[grid[n-i-1][m-j-1]]++;
                ans += min(cnt[0], cnt[1]);
            }
        }

        int tmp = 0, cnt = 0;
        if(n%2){
            for(int j = 0; j < m/2; j++){
                tmp += (grid[n/2][j]!=grid[n/2][m-j-1]);
                cnt += grid[n/2][j] + grid[n/2][m-j-1];
            }
        }
        if(m%2){
            for(int i = 0; i < n/2; i++){
                tmp += (grid[i][m/2]!=grid[n-i-1][m/2]);
                cnt += (grid[i][m/2] + grid[n-i-1][m/2]);
            }
        }
        if(n%2 && m%2){
            // cnt += grid[n/2][m/2];
            if(grid[n/2][m/2] == 1)
                ans++;
        }
        // cout << ans << " " << tmp << " " << cnt << endl;
        if(tmp == 0) ans += cnt%4;
        return tmp + ans;
    }
};
