class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size(), ans = 0, temp = 0;

        for (int i = 0, j = n-1; i < j; i++, j--) {
            for (int x = 0, y = m-1; x < y; x++, y--) {
                temp = grid[i][x] + grid[i][y] + grid[j][x] + grid[j][y];
                ans += min(temp, 4-temp);
            }
        }

        int noteq = 0, one = 0;

        if (n&1) 
            for (int i = 0, j = m-1; i < j; i++, j--)
                if (grid[n/2][i] != grid[n/2][j]) noteq++;
                else if (grid[n/2][i]) one += 2;

        if (m&1)
            for (int i = 0, j = n-1; i < j; i++, j--)
                if (grid[i][m/2] != grid[j][m/2]) noteq++;
                else if (grid[i][m/2]) one += 2;

        if (n&m&1)
            ans += grid[n/2][m/2];

        if (one % 4 == 0) {
            ans += noteq;
        } else {
            if (noteq) {
                ans += noteq;
            } else {
                ans += 2;
            }
        }

        return ans;
    }
};
