class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size();
        int ans = 0;
        for(int i=0; i<n/2; i++){
            for(int j=0; j<m/2; j++){
                vector<int> cnt(2,0);
                cnt[a[i][j]]++;
                cnt[a[i][m-1-j]]++;
                cnt[a[n-1-i][j]]++;
                cnt[a[n-1-i][m-1-j]]++;
                ans += 4-max(cnt[0], cnt[1]);
            }
        }
        int c1=0, cc=0;
        if(n%2==1){
            for(int j=0; j<m/2; j++){
                if(a[n/2][j]!=a[n/2][m-1-j]){cc++;ans++;}
                else if(a[n/2][j]==1)c1++;
            }
        }
        if(m%2==1){
            for(int i=0; i<n/2; i++){
                if(a[i][m/2]!=a[n-1-i][m/2]){cc++;ans++;}
                else if(a[i][m/2]==1)c1++;
            }
        }
        if(n%2==1 && m%2==1){
            ans += a[n/2][m/2];
        }
        if(c1%2==1 && cc==0)ans+=2;
        return ans;
    }
};
