class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size();
        int inf = 1e9;
        vector<int> f(4, inf);
        f[0] = 0;
        for(int i = 0; i <= n - i - 1; ++i)
        {
            for(int j = 0; j <= m - j - 1; ++j)
            {
                auto g = f;
                f.assign(4, inf);
                set<pair<int,int>> st;
                st.insert({i, j}), st.insert({n - i - 1, j}), st.insert({n - i - 1, m - j - 1}), st.insert({i, m - j - 1});
                for(int x = 0; x < 4; ++x)
                {
                    for(int t = 0; t < 2; ++t)
                    {
                        int c = 0, v = 0;
                        for(auto [x, y] : st)
                        {
                            c++;
                            v+=(t != a[x][y]);
                        }
                        f[(x + c * t)%4] = min(f[(x + c * t)%4], g[x] + v);
                    }
                }
            }
        }
        return  f[0];
    }
};
