class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        map<int,int> row,col;
        int n=grid.size(),m=grid[0].size();
        int l=0,r=grid.size()-1;
        while(l<=r) {
            row[l]=r;
            row[r]=l;
            l++;
            r--;
        }
        l=0,r=grid[0].size()-1;
        while(l<=r) {
            col[l]=r;
            col[r]=l;
            l++;
            r--;
        }
        vector<vector<bool>> v(n+1,vector<bool> (m+1,false));
        int ans1=0;
        priority_queue<int> q;
        int ans2=0;
        for(int i=0;i<grid.size();i++) {
            for(int j=0;j<grid[0].size();j++) {
                if(!v[i][j]) {
                    int z=0,o=0;
                    queue<pair<int,int>> q1;
                    int sub=1;
                    q1.push({i,j});
                    while(!q1.empty()) {
                        int si=q1.size();
                        while(si--) {
                            pair<int,int> p=q1.front();
                            q1.pop();
                            int i1=p.first,j1=p.second;
                            if(grid[i1][j1]==0) z++;
                            else o++;
                            v[i1][j1]=true;
                            int d1=row[i1],d2=col[j1];
                            if(!v[d1][j1]) {
                                q1.push({d1,j1});sub++;
                                v[d1][j1]=true;}
                            if(!v[i1][d2]) {
                                q1.push({i1,d2});sub++;
                                v[i1][d2]=true;}
                        }
                    }
                    if(sub==4) ans1+=min(o,z);
                    else if(sub==2){
                        q.push(o);
                        ans2+=o;
                    }
                    else ans1+=o;
                    //cout<<i<<" "<<j<<" "<<ans<<"\n";
                }
            }
        }
        ans=ans1+ans2;
        //cout<<ans<<" bello\n";
        while(q.size()>=2) {
            int d1=q.top();q.pop();
            int d2=q.top();q.pop();
            int v1=2-d1,v2=2-d2;
            ans2-=d1;ans2-=d2;
            ans2+=v1;ans2+=v2;
            ans=min(ans,ans1+ans2);
        }
        return ans;
    }
};
