class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n = len(grid)
        m = len(grid[0])

        current = 0

        for i in range(n // 2):
            for j in range(m // 2):

                summation = grid[i][j] + grid[n - i - 1][j] + grid[i][m - j - 1] + grid[n - i - 1][m - j - 1]

                current += min(4 - summation, summation)

        minimum = current

        current = 0
        amount = 0

        if n % 2 == 1 and m % 2 == 1:
            for j in range(m // 2):
                if grid[n // 2][j] != grid[n // 2][m - j - 1]:
                    current += 1
                elif grid[n // 2][j] == 1:
                    amount += 1

            for i in range(n // 2):
                if grid[i][m // 2] != grid[n - i - 1][m // 2]:
                    current += 1
                elif grid[i][m // 2] == 1:
                    amount += 1

            if amount % 2 == 1 and current == 0:
                current += 2

            if grid[n // 2 ][m // 2] == 1:
                current += 1

        elif n % 2 == 1:


            for j in range(m // 2):
                if grid[n // 2][j] != grid[n // 2][m - j - 1]:
                    current += 1
                elif grid[n // 2][j] == 1:
                    amount += 1

            if amount % 2 == 1 and current == 0:
                current += 2

        elif m % 2 == 1:
            for i in range(n // 2):
                if grid[i][m // 2] != grid[n - i - 1][m // 2]:
                    current += 1
                elif grid[i][m // 2] == 1:
                    amount += 1

            if amount % 2 == 1 and current == 0:
                current += 2



        return minimum + current
