class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int ans=0;
        for(int i=0;i<((n)/2);i++){
            for(int j=0;j<((m)/2);j++){
                int a=grid[i][j]+grid[n-1-i][j]+grid[i][m-1-j]+grid[n-1-i][m-1-j];
                ans+=min(a,4-a);
            }
        }
        int cnt=0;
        int ones=0;
        if(n%2==1){
            for(int j=0;j<((m)/2);j++){
                 if(grid[(n/2)][j]!=grid[n/2][m-1-j]){
                    cnt++;
                 }
                 else if(grid[n/2][j]==1){
                        ones+=2;
                 }
            }
        }
        if(m%2==1){
            for(int i=0;i<((n)/2);i++){
                if((grid[i][(m/2)])!=grid[n-1-i][(m/2)]){
                    cnt++;
                }
                else if((grid[i][(m/2)])==1){
                    ones+=2;
                }
            }
        }
        if((n%2==1)&&(m%2==1)){
            if(grid[(n-1)/2][(m-1)/2]==1){
            ones++;
            }
        }
        if(ones%4==0){
            return ans+cnt;
        }
        else if(ones%4==1){
            return ans+cnt+1;
        }
        else if(ones%4==2){
            if(cnt>0){
                   return ans+cnt;}
                   else{
                    return ans+2;
                   }

        }
        else{
            if(cnt>0){
                   return ans+cnt+1;}
                   else{
                    return ans+3;
                   }

        }

    }
};
