class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();
        int ans = 0;
        for(int i = 0; i < m / 2; i++) {
            for(int j = 0; j < n / 2; j++) {
                int tmp = grid[i][j] + grid[m-i-1][n-j-1] + grid[m-i-1][j] + grid[i][n-j-1];
                ans += min(tmp, 4-tmp);
            }
        }
        int col_num_11 = 0, col_num_01 = 0;
        if(m & 1) {
            col_num_11 = 0, col_num_01 = 0;
            for(int j = 0; j < n / 2; j++) {
                if(grid[m/2][j] == grid[m/2][n-j-1] && grid[m/2][j] == 1) {
                    col_num_11++;
                } else if (grid[m/2][j] != grid[m/2][n-j-1]) {
                    col_num_01++;
                }
            }
        }
        int row_num_11 = 0, row_num_01 = 0;
        if(n & 1) {
            for(int i = 0; i < m / 2; i++) {
                if(grid[i][n/2] == grid[m-i-1][n/2] && grid[i][n/2] == 1) {
                    row_num_11++;
                } else if (grid[i][n/2] != grid[m-i-1][n/2]) {
                    row_num_01++;
                }
            }
        }
        if((m & 1) && (n & 1)) {
            ans += grid[m/2][n/2];
        }
        if(row_num_01 > 0 || col_num_01 > 0) {
            ans += row_num_01 + col_num_01;
        } else {
            ans += ((row_num_11 + col_num_11) & 1) * 2;
        }
        return ans;
    }
};
