class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int n = grid.size(), m = grid[0].size(), ans = 0;

        for(int i=0;i<n/2;i++) {
            for(int j=0;j<m/2;j++) {
                int x = grid[i][j];
                int y = grid[i][m - j - 1];
                int a = grid[n - i - 1][j];
                int b = grid[n - i - 1][m - j - 1];

                int one = x + y + a + b; 
                ans += min(one, 4 - one);
            }
        }

        //cout<<ans<<"\n";

        if(n % 2 == 0 && m % 2 == 0) return ans; 

        if((n + m)%2) {
            if(n % 2) {
                int x = 0, y = 0; 
                for(int j=0;j<m/2;j++) {
                    x += (grid[n/2][j] && grid[n/2][m-j-1]);
                    y += (grid[n/2][j] != grid[n/2][m-j-1]);
                }
                if(y == 0 && x % 2) ans += 2; 
                ans += y; 
            }else {
                int y = 0, x = 0; 
                for(int i=0;i<n/2;i++) {
                    x += grid[i][m/2] && grid[n-i-1][m/2];
                    y += grid[i][m/2] != grid[n-i-1][m/2];
                }
                if(y == 0 && x % 2) ans += 2; 
                ans += y; 
            }
            return ans; 
        }
        // both n and m are odd; 

        int x = 0, y = 0; 
        for(int i=0;i<n/2;i++) {
            x += grid[i][m/2] && grid[n-i-1][m/2];
            y += grid[i][m/2] != grid[n-i-1][m/2];
        }
        for(int j=0;j<m/2;j++) {
            x += (grid[n/2][j] && grid[n/2][m-j-1]);
            y += (grid[n/2][j] != grid[n/2][m-j-1]);
        }
        if(y == 0 && x%2) ans += 2; 
        else ans += y; 

        ans += grid[n/2][m/2];

        return ans; 
    }

};
