class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int cnt=0;
        // for(int i=0;i<n;i++)
        // {
        //     for(int j=0;j<m;j++)
        //     {
        //         if(grid[i][j]==1)
        //         cnt++;
        //     }
        // }
        int cnt1=0;
        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
                int x=grid[i][j];
                int count=1;
                if(grid[n-1-i][j] == x)
                count++;

                if(grid[i][m-1-j]==x)
                count++;

                if(grid[n-1-i][m-1-j]==x)
                count++;

                cnt1+=min(count,4-count);
            }
        }
        int c1=0;
        int cnt2=0;
        if(n%2==1)
        for(int j=0;j<m/2;j++)
        {
            if(grid[n/2][j]!=grid[n/2][m-1-j])
            {
                cnt2++;
            }
            else
            {
                if(grid[n/2][j]==1)
                c1+=2;
            }
        }

        // if(c1%4==0 || cnt2>=1)
        // {
            cnt1+=cnt2;
        // }
        // else
        // {
        //     cnt1+=2;
        // }

        int c2=0;
        int cnt3=0;
        if(m%2==1)
        for(int i=0;i<n/2;i++)
        {
            if(grid[i][m/2]!=grid[n-1-i][m/2])
            {
                cnt3++;
            }
            else
            {
                if(grid[i][m/2]==1)
                c2+=2;
            }
        }

        // if(c2%4==0 || cnt3>=1)
        // {
            cnt1+=cnt3;
        // }
        // else
        // {
        //     cnt1+=2;
        // }
        if((c1+c2)%4!=0 && (cnt2+cnt3)<1)
        {
            cnt1+=2;
        }
        if(n%2==1 && m%2==1)
        {
            if(grid[n/2][m/2]==1)
            cnt1+=1;
        }
        return cnt1;
    }
};
