class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size(),n=grid[0].size();
        vector<vector<bool>> vis(m,vector<bool>(n,false));
        int ans=0;
        int c1=0,c2=0,c3=0;
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(vis[i][j]) continue;
                vis[i][j]=true;
                int ct0=0,ct1=0;
                if(grid[i][j]==0) ct0++;
                else ct1++;
                int i1,j1;
                i1=m-1-i,j1=j;
                if(!vis[i1][j1]){
                    vis[i1][j1]=true;
                    if(grid[i1][j1]==0) ct0++;
                    else ct1++;
                }

                i1=i,j1=n-1-j;
                if(!vis[i1][j1]){
                    vis[i1][j1]=true;
                    if(grid[i1][j1]==0) ct0++;
                    else ct1++;
                }

                i1=m-1-i,j1=n-1-j;
                if(!vis[i1][j1]){
                    vis[i1][j1]=true;
                    if(grid[i1][j1]==0) ct0++;
                    else ct1++;
                }

                if(ct0+ct1==4){
                    ans+=min(ct0,ct1);
                }else if(ct0+ct1==2){
                    if(ct0==ct1) {
                        ans++;
                        c3++;
                    }else if(ct1==2) c2++;
                }else if(ct0+ct1==1) {
                    if(ct1==1) c1++;
                }
            }
        }

        int rem=(2*c2+c1)%4;
        if(rem==1){
           ans++; 
        }else if(rem==2){
            if(c3==0) ans+=2;
        }else if(rem==3){
            if(c3>0) ans++;
            else ans+=3;
        }

        return ans;
    }
};
