class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int flip=0;
        int m=grid.size(),n=grid[0].size();
        for(int h=0,i=m-1;h<i;h++,i--)
            for(int j=0,k=n-1;j<k;j++,k--){
                int ones=grid[h][j]+grid[h][k]+grid[i][j]+grid[i][k];
                if(ones<=2)
                    flip+=ones;
                else
                    flip+=(4-ones);
            }
        if(m%2==1&&n%2==1)
            flip+=grid[m/2][n/2];
        int ones=0;
        if(m%2==1){
            for(int j=0,k=n-1;j<k;j++,k--)
                if(grid[m/2][j]!=grid[m/2][k])
                    ones=-100,flip++;
                else if(grid[m/2][j]==1&&ones>=0)
                    ones+=2;
        }
        if(n%2==1){
            for(int h=0,i=m-1;h<i;h++,i--)
                if(grid[h][n/2]!=grid[i][n/2])
                    ones=-100,flip++;
                else if(grid[h][n/2]&&ones>=0)
                    ones+=2;
        }
        if(ones>=0&&ones%4!=0)
            flip+=2;
        return flip;
    }
};
