class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m,n = len(grid),len(grid[0])
        ans = 0
        for i in range(m//2):
            for j in range(n//2):
                x = grid[i][j]+grid[i][n-1-j]+grid[m-1-i][j]+grid[m-1-i][n-1-j]
                if x<=2:
                    ans += x
                else:
                    ans += 4-x
        cnt = cnt1 = 0
        if m%2 and n%2:
            for i in range(m//2):
                if grid[i][n//2] != grid[m-1-i][n//2]:
                    cnt += 1
                if grid[i][n//2] == 1 and grid[m-1-i][n//2] == 1:
                    cnt1 += 1
            for j in range(n//2):
                if grid[m//2][j] != grid[m//2][n-1-j]:
                    cnt += 1
                if grid[m//2][j] == 1 and grid[m//2][n-1-j] == 1:
                    cnt1 += 1
            if cnt1%2==1 and cnt==0:
                return ans+2 if grid[m//2][n//2]==0 else ans+3
            return ans+cnt if grid[m//2][n//2]==0 else ans+cnt+1
        elif m%2:
            for j in range(n//2):
                if grid[m//2][j] != grid[m//2][n-1-j]:
                    cnt += 1
                if grid[m//2][j] == 1 and grid[m//2][n-1-j] == 1:
                    cnt1 += 1
            if cnt1%2==1 and cnt==0:
                return ans+2
            return ans+cnt
        elif n%2:
            for i in range(m//2):
                if grid[i][n//2] != grid[m-1-i][n//2]:
                    cnt += 1
                if grid[i][n//2] == 1 and grid[m-1-i][n//2] == 1:
                    cnt1 += 1
            if cnt1%2==1 and cnt==0:
                return ans+2
            return ans+cnt
        return ans
