class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int ans=0;
        if(n%2==1 && m%2==1 && grid[n/2][m/2]==1)ans+=1;
        bool flag=false;
        int c=0;
        if(n%2==1){
            for(int i=0;i<m/2;i++){
                if(grid[n/2][i]!=grid[n/2][m-i-1])flag=true;
                else if(grid[n/2][i]==1)c+=1;
            }
        }
        if(m%2==1){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-1-i][m/2])flag=true;
                else if(grid[i][m/2]==1)c+=1;
            }
        }
        if(c%2==0 || flag==true)ans+=0;
        else ans+=2;
        vector<vector<int>>vis(n,vector<int>(m,0));
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(!vis[i][j]){
                    int ones=0,zeros=0;
                    if(grid[i][j]==1)ones++;
                    else zeros++;
                    if((m-j-1)!=j && grid[i][m-j-1]==1)ones++;
                    else if((m-j-1)!=j && grid[i][m-j-1]==0)zeros++;
                    if((n-i-1)!=i && grid[n-i-1][j]==1)ones++;
                    else if((n-i-1)!=i && grid[n-1-i][j]==0)zeros++;
                    if((m-j-1)!=j && (n-1-i)!=i && grid[n-1-i][m-j-1]==1)ones++;
                    else if((m-j-1)!=j && (n-1-i)!=i && grid[n-1-i][m-j-1]==0)zeros++;
                    ans+=min(ones,zeros);
                    vis[i][j]=1;
                    vis[n-i-1][j]=1;
                    vis[i][m-1-j]=1;
                    vis[n-1-i][m-j-1]=1;
                }
            }
        }
        return ans;
    }
};
