class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size();
        int ans = 0, twos = 0;

        for (int i = 0; i < n /2;i++) {
            for (int j = 0; j < m /2;j++) {
                vector<int> p(2, 0);
                set<pair<int, int>> s;

                if (s.count({i, j}) == 0) p[a[i][j]]++, s.insert({i, j});
                if (s.count({i, m-1-j}) == 0) p[a[i][m-1-j]]++, s.insert({i, m-1-j});
                if (s.count({n-1-i, j}) == 0) p[a[n-1-i][j]]++, s.insert({n-1-i, j});
                if (s.count({n-1-i, m-1-j}) == 0) p[a[n-1-i][m-1-j]]++, s.insert({n-1-i, m-1-j});

                int sum = p[0] + p[1];
                assert(sum == 4);
                ans += min(p[0], p[1]); 
            }
        }

        int matched = 0;
        int unmatched = 0;

        if (n % 2 == 1) {
            // vector<int> p(2, 0);
            for (int j = 0; j < m /2;j++) {
                if (a[n / 2][j] != a[n / 2][m - 1 - j]) {
                    unmatched++;
                } else if (a[n/2][j] == 1) {
                    matched++;
                }
            }
        }

        if (m % 2 == 1) {
            // int cnt = 0;
            for (int i = 0; i < n / 2;i++) {
                if (a[i][m / 2] != a[n - 1- i][m / 2]) {
                    unmatched++;
                } else if (a[i][m/2] == 1) {
                    matched++;
                }
            }
        }

        if (matched % 2 == 1) {
            if (unmatched == 0) ans += 2;
            else {
                unmatched -= 1;
                ans++;
            }
        }

        if (unmatched > 0) {
            ans += unmatched;
        }

        if (n % 2== 1 and m%2== 1 and a[n/2][m/2] == 1) ans++;

        return ans;
    }
};
