class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int ans=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int c=0;
                if(grid[i][j]==0){
                    c++;
                }
                if(grid[n-1-i][j]==0){
                    c++;
                }
                if(grid[n-1-i][m-1-j]==0){
                    c++;
                }
                if(grid[i][m-1-j]==0){
                    c++;
                }
                ans+=min(c,4-c);
            }
        }
        int c1=0;
            int c2=0;
        if(n%2==1){
            if(m%2==1){
                if(grid[n/2][m/2]==1){
                    grid[n/2][m/2]=0;
                    ans++;
                }
            }
            int j=0;

            while(j<m/2){
                if(grid[n/2][j]!=grid[n/2][m-1-j]){
                    c2++;
                }
                else if(grid[n/2][j]==1){
                    c1+=2;
                }
                j++;
            }

        }
        if(m%2==1){
            if(n%2==1){
                if(grid[n/2][m/2]==1){
                    grid[n/2][m/2]=0;
                    ans++;
                }
            }
            int j=0;
            // int c1=0;
            // int c2=0;
            while(j<n/2){
                if(grid[j][m/2]!=grid[n-1-j][m/2]){
                    c2++;
                }
                else if(grid[j][m/2]==1){
                    c1+=2;
                }
                j++;
            }

        }
        if(c1%4==2){
                if(c2!=0){
                    ans+=c2;
                }
                else{
                    ans+=2;
                }
            }
            else{
            ans+=c2;
            }
        return ans;
    }
};
