class Solution {
    public int minFlips(int[][] grid) {
        int r = grid.length;
        int c = grid[0].length;
        int m=r/2;
        int n=c/2;
        int ans=0;

        for(int i=0; i<m; i++) {
            for(int j=0; j<n; j++) {

                int one = grid[i][j] + grid[i][c-j-1] + grid[r-i-1][j] + grid[r-i-1][c-j-1];
                ans += Math.min(one , 4-one);

            }
        }

        if(r%2==0 && c%2==0) {
            return ans;
        }
        int x=0;
        boolean ok = false;
        if(r%2 != 0) {
            int s=0;
            int e=c-1;
            while(s<e) {
                if(grid[r/2][s] == 1 && grid[r/2][e] ==1) {
                    x++;
                }
                else if (grid[r/2][s] != grid[r/2][e]) {
                    ans++;
                    ok = true;
                }
                s++;
                e--;
            }
        }
        if(c%2 != 0) {
            int s=0;
            int e=r-1;
            while(s<e) {
                if(grid[s][c/2] == 1 && grid[e][c/2] ==1) {
                    x++;
                }
                else if (grid[s][c/2] != grid[e][c/2]) {
                    ans++;
                    ok = true;
                }
                s++;
                e--;
            }
        }
        if(r%2 != 0 && c%2 != 0) ans += grid[r/2][c/2];

        if(x%2 == 0) return ans;
        if(ok) return ans;
        return ans+2;
    }
}
