class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        ind = set()
        for i in range(len(grid)):
            for j in range(len(grid[0])):
                a = tuple([i, j])
                b = tuple([len(grid) - i - 1, j])
                c = tuple([i, len(grid[0]) - j - 1])
                d = tuple([len(grid) - i - 1, len(grid[0]) - j - 1])
                ind.add(tuple(sorted(set([a, b, c, d]))))
        res = 0
        onezero = zerozero = oneone = 0
        for cur in ind:
            if len(cur) == 1 and grid[cur[0][0]][cur[0][1]] == 1:
                res += 1
            elif len(cur) == 2:
                # print(cur)
                one = zero = 0
                for i, j in cur:
                    if grid[i][j] == 1:
                        one += 1
                    else:
                        zero += 1
                if one == 2:
                    oneone += 1
                elif zero == 2:
                    zerozero += 1
                else:
                    onezero += 1
            else:
                one = zero = 0
                for i, j in cur:
                    if grid[i][j] == 1:
                        one += 1
                    else:
                        zero += 1
                res += min(one, zero)
        if oneone % 2 == 0:
            res += onezero
        else:
            if onezero:
                res += onezero
            else:
                res += 2
        return res
