class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();

        if(n==1){
            if(m<4){
                int s = 0;
                for(int j=0;j<m;j++)
                    s += grid[0][j];
                return s;
            }
        }
        if(m==1){
            if(n<4){
                int s = 0;
                for(int i=0;i<n;i++)
                    s += grid[i][0];
                return s;
            }
        }

        int ans = 0;

        if(n%2==1 && m%2==1){
            ans += grid[n/2][m/2];
            grid[n/2][m/2] = 0;
        }

        for(int i=0; i<n/2; i++){
            for(int j=0; j<m/2; j++){
                int c = grid[i][j] + grid[n-1-i][j] + grid[i][m-1-j] + grid[n-1-i][m-1-j];
                ans += min(c, 4-c);
            }
        }


        int misRow = 0;

        int one = 0;

        if(n%2==1){
            for(int j=0;j<m/2;j++)
                if(grid[n/2][j]!=grid[n/2][m-1-j]){
                    misRow++;
                    one++;
                }
                else if(grid[n/2][j]==1)
                    one+=2;
        }
        int misCol = 0;
        if(m%2==1){
            for(int i=0;i<n/2;i++)
                if(grid[i][m/2]!=grid[n-1-i][m/2]){
                    misCol++;
                    one++;
                }
                else if(grid[i][m/2]==1){
                    one+=2;
                }
        }
        // cout<<one<<"\n";
        ans += (misRow + misCol);
        if((misRow + misCol)==0 && one%4!=0)
            ans += 2;
        return ans;
    }
};
