class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        int ans = 0;
        for(int i = 0; i < m/2; i++)
            for(int j = 0; j < n/2; j++)
            {
                int x = 0;
                if(grid[i][j])
                    x++;

                if (grid[i][n-j-1])
                    x++;

                if (grid[m-i-1][j])
                    x++;

                if (grid[m-i-1][n-j-1])
                    x++;
                ans+=min(x,4-x);
            }

        if (m%2 && n%2){
            if (grid[m/2][n/2]==1)
                ans++;

            bool changes=0;
            int ones=0;
            for(int i = 0; i < m/2; i++){
                if(grid[i][n/2] != grid[m-i-1][n/2]){
                    ans++;
                    changes=1;
                }

                else if (grid[i][n/2])
                    ones=1-ones;        
            }

            for(int i = 0; i < n/2; i++){
                if(grid[m/2][i] != grid[m/2][n-i-1]){
                    ans++;
                    changes=1;
                }

                else if (grid[m/2][i])
                    ones=1-ones;        
            }

            if (!changes && ones)
                ans+=2;
        }

        else if (m%2)
        {
            bool changes=0;
            int ones=0;
            for(int i = 0; i < n/2; i++){
                if(grid[m/2][i] != grid[m/2][n-i-1]){
                    ans++;
                    changes=1;
                }

                else if (grid[m/2][i])
                    ones=1-ones;        
            }

            if (!changes && ones)
                ans+=2;            
        }

        else if (n%2)
        {
            bool changes=0;
            int ones=0;
            for(int i = 0; i < m/2; i++){
                if(grid[i][n/2] != grid[m-i-1][n/2]){
                    ans++;
                    changes=1;
                }

                else if (grid[i][n/2])
                    ones=1-ones;        
            }

            if (!changes && ones)
                ans+=2;
        }
        return ans;
    }
};
