class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n,m = len(grid), len(grid[0])
        ret = 0
        for i in range(n//2):
            for j in range(m//2):
                t = grid[i][j] + grid[n-1-i][j] + grid[i][m-1-j] + grid[n-1-i][m-1-j]
                ret += min(t,4-t)

        v = 0
        v1 = 0
        v2 = 0
        if n % 2:
            for i in range(m // 2):
                if grid[n//2][i] != grid[n//2][m-1-i]:
                    v += 1
                elif grid[n//2][i]:
                    v2 += 1

        if m % 2:
            for i in range(n // 2):
                if grid[i][m//2] != grid[n-1-i][m//2]:
                    v += 1
                elif grid[i][m//2]:
                    v2 += 1
        if v == 0 and v2 & 1:
            ret += 2
        if v:
            ret += v

        if n % 2 and m % 2:
            ret += grid[n//2][m//2]

        return ret
