class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        ans = 0
        n, m = len(grid), len(grid[0])
        ones, two = 0, 2
        twos = 0
        for i in range((n + 1) // 2):
            for j in range((m + 1) // 2):
                if i == len(grid) // 2 and j == len(grid[0]) // 2:
                    ones += grid[i][j]
                elif i == len(grid) // 2:
                    if grid[i][j] != grid[i][m - j - 1]:
                        ans += 1
                        two = 0
                    else:
                        twos += 2 * grid[i][j]
                        ones += 2 * grid[i][j]
                elif j == len(grid[0]) // 2:
                    if grid[i][j] != grid[n - i - 1][j]:
                        ans += 1
                        two = 0
                    else:
                        twos += 2 * grid[i][j]
                        ones += 2 * grid[i][j]
                else:
                    a, b, c, d = grid[i][j], grid[n - i - 1][j], grid[i][m - j - 1], grid[n - i - 1][m - j - 1]
                    one = sum([a, b, c, d])
                    ans += min(one, 4 - one)
        if ones % 4 == 0:
            return ans
        if twos % 4 == 0:
            return ans + 1
        return ans + two + ones % 2
