class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m,n=len(grid),len(grid[0])
        res=0
        for i in range(m//2):
            for j in range(n//2):
                s=sum([grid[i][j],grid[i][-j-1],grid[-i-1][j],grid[-i-1][-j-1]])
                res+=min(4-s,s)
        d=0
        s=0
        # s is sum of element on middle row and col
        if m%2==1:
            s+=sum(grid[m//2])
            for i in range(n//2):
                if grid[m//2][i]!=grid[m//2][-i-1]:
                    d+=1
        if n%2==1:
            s+=sum([i[n//2] for i in grid])
            for i in range(m//2):
                if grid[i][n//2]!=grid[-i-1][n//2]:
                    d+=1
        if m%2==1 and n%2==1 and grid[m//2][n//2]==1:
            res+=1 
            s-=2
        if d==0:
            return res if s%4==0 else res+2
        elif d==1:
            if (s+1)%4==0 or (s-1)%4==0:
                return res+1
            else:
                return res+2
        return res+d
