class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
          int n=grid.size();
          int m=grid[0].size();

          int ans=0;
          int t1=0;
          int t2=0;
          int t3=0;

      for(int i=0; i<grid.size()/2+(n%2==0?0:1); i++){

        for(int j=0; j<grid[0].size()/2+(m%2==0?0:1); j++){

            set<pair<int,int>> s1;

          s1.insert({i,j});

           s1.insert({i,m-1-j});

           s1.insert({n-1-i,j});

           s1.insert({n-1-i,m-1-j});



            if(s1.size()==4){

                int ct=0;
                for(auto it:s1){
                    if(grid[it.first][it.second]==1){
                        ct++;
                    }

                }

               if(ct<=2){

                ans+=ct;
               }
               else{

                ans+=4-ct;


               }


            }
            else if(s1.size()==2){


             int ct=0;
                for(auto it:s1){
                    if(grid[it.first][it.second]==1){
                        ct++;
                    }

                }
            //   cout<<ct<<endl;
               if(ct==0){

                ans+=ct;
               }
               else if(ct==1){

                ans++;
                t3++;
               }
               else{
                t1++;
               }

            }
            else{

                 int ct=0;
                for(auto it:s1){
                    if(grid[it.first][it.second]==1){
                        ct++;
                    }

                }

                if(ct==1)t2++;
            }


          }

      }


     if(t2==1)ans++;

       if(t1%2!=0){
        if(t3==0)
        ans+=2;
       }


       return ans;
    }
};
