class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size(), ans = 0, cnt;
        for(int i = 0; i < n / 2; i++) {
            for(int j = 0; j < m / 2; j++) {
                cnt = grid[i][j] + grid[n - 1 - i][j] + grid[i][m - 1 - j] + grid[n - 1 - i][m - 1 - j];
                ans += min(cnt, 4 - cnt);
            }
        }
        int cnt1 = 0, cnt2 = 0, cnt3 = 0, cnt4 = 0;
        if(n%2 == 1) {
            for(int i = 0 ; i < m / 2; i++) {
                cnt1 += (grid[n/2][i] != grid[n/2][m - 1 - i]);
                cnt2 += grid[n/2][i] + grid[n/2][m - 1 - i];
            }
        }
        if(m%2 == 1) {
            for(int i = 0 ; i < n / 2; i++) {
                cnt3 += (grid[i][m/2] != grid[n - 1 - i][m/2]);
                cnt4 += grid[i][m/2] + grid[n - 1 - i][m/2];
            }
        }
        if(n%2 == 1 && m%2 == 1) {
            ans += grid[n/2][m/2];
            grid[n/2][m/2] = 0;
            if((cnt1 + cnt2 + cnt3 + cnt4)%4 != 0) {
                if(cnt1 == 0 && cnt3 == 0) cnt1 = 2;
            }
            ans += cnt1 + cnt3;
        } else if (n%2 == 1) {
            if((cnt1 + cnt2)%4 != 0) {
                if(cnt1 == 0) cnt1 = 2;
            }
            ans += cnt1;
        } else if (m%2 == 1) {
            if((cnt3 + cnt4)%4 != 0) {
                if(cnt3 == 0) cnt3 = 2;
            }
            ans += cnt3;
        }
        return ans;
    }
};
