class Solution {
    public int minFlips(int[][] a) {
        int m = a.length, n = a[0].length;
        int sum = 0;
        for (int i = 0; i < m / 2; i++) {
            for (int j = 0; j < n / 2; j++) {
                int cnt = a[i][j] + a[i][n - 1 - j] + a[m - 1 - i][j] + a[m - 1 - i][n - 1 - j];
                if (cnt == 1 || cnt == 3) {
                    sum++;
                } else if (cnt == 2) {
                    sum += 2;
                }
            }
        }
        boolean flip = false;
        int cnt1 = 0;
        if (m % 2 == 1) {
            for (int j = 0; j < n / 2; j++) {
                if (a[m / 2][j] != a[m / 2][n - 1 - j]) {
                    sum++;
                    flip = true;
                } else if (a[m / 2][j] == 1) {
                    cnt1 += 2;
                }
            } 
        }
        if (n % 2 == 1) {
            for (int i = 0; i < m / 2; i++) {
                if (a[i][n / 2] != a[m - 1 - i][n / 2]) {
                    sum++;
                    flip = true;
                } else if (a[i][n / 2] == 1) {
                    cnt1 += 2;
                }
            }
        }
        if (!flip && cnt1 % 4 != 0) sum += 2;
        if (m % 2 == 1 && n % 2 == 1) sum += a[m / 2][n / 2];
        return sum;
    }

