class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int k = n / 2, l = m / 2;
        map<pair<int, int>, int> mp, mp1;
        int ans = 0;
        for(int i = 0; i < k; i++) {
            for(int j = 0; j < l; j++) {
                int c0 = 0, c1 = 0;
                if(grid[i][j] == 1) c1++;
                else c0++;
                if(grid[i][m - 1 - j] == 1) c1++;
                else c0++;
                if(grid[n - 1 - i][j] == 1) c1++;
                else c0++;
                if(grid[n - 1 - i][m - 1 - j] == 1) c1++;
                else c0++;
                int a = abs(4 - c0), b = (4 - c1);
                if(a > b) swap(a, b);
                if(c0 == 4 || c1 == 4) continue;
                ans += min(a, b);
            }
        }
        int cnt = 0, cnt1 = 0;
        if(m & 1) {
            for(int i = 0; i < k; i++) {
                if(grid[i][l] != grid[n - 1 - i][l]) cnt++;
                else {
                    if(grid[i][l] == 1) cnt1++;
                }
            }
        }
        if(n & 1) {
            for(int i = 0; i < l; i++) {
                if(grid[k][i] != grid[k][m - 1 - i]) cnt++;
                else {
                    if(grid[k][i] == 1) cnt1++;
                }
            }
        }
        if((m & 1) && (n & 1)) {
            if(grid[k][l] == 1) ans++;
        }
        if((cnt1 % 2 == 0)) {
            ans += cnt;
        }
        else {
            if(cnt) ans += cnt;
            else {
                ans += 2;
            }
        }
        return ans;
    }
};
