class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {


        int n = grid.size();
        int m = grid[0].size();

        int ans = 0;

        if (n % 2 && m % 2 && grid[n / 2][m / 2])
        {
            ans ++;
        }
        for (int i = 0; i < n / 2; i++)
        {
            int start = 0, end = m - 1;

            while (start < end)
            {
                unordered_map < int, int > mp;
                mp[grid[i][start]] ++;
                mp[grid[i][end]] ++;

                mp[grid[n - 1 - i][start]] ++;
                mp[grid[n - 1 - i][end]] ++;

                int num = max(mp[0], mp[1]);
                ans += 4 - num;

                start ++;
                end --;
            }
        }


        int change = 0;
        int non_change_sum = 0;
        if (n % 2)
        {
            int start = 0, end = m - 1;
            while (start < end)
            {
                if (grid[n / 2][start] != grid[n / 2][end])
                {
                    ans ++;
                    change ++;
                }

                else
                {
                    if (grid[n / 2][start])
                    {
                        non_change_sum += 2;
                    }
                }
                start ++;
                end --;
            }
        }


        if (m % 2)
        {
            int start = 0, end = n - 1;
            while (start < end)
            {
                if (grid[start][m / 2] != grid[end][m / 2])
                {
                    ans ++;
                    change ++;
                }

                else
                {
                    if (grid[start][m / 2])
                    {
                        non_change_sum += 2;
                    }
                }
                start ++;
                end --;
            }
        }

