class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        int count = 0;
        bool par = false;
        int par2 = 0;
        for (int i=0;i<m/2;i++){
            for (int j=0;j<n/2;j++){
                int c = grid[i][j]+grid[m-1-i][j]+grid[m-1-i][n-1-j]+grid[i][n-1-j];
                if (c==1||c==3){
                    count++;
                }
                else if (c==2){
                    count+=2;
                }
            }
        }
        if (m%2==1&&n%2==1&&grid[m/2][n/2]==1){
            count++;
        }
        if (m%2==1){
            for (int j=0;j<n/2;j++){
                if (grid[m/2][j]!=grid[m/2][n-1-j]){
                    par = true;
                    count++;
                }
                else if (grid[m/2][j]==grid[m/2][n-1-j]&&grid[m/2][j]==1){
                    par2++;
                }
            }
        }
        if (n%2==1){
            for (int i=0;i<m/2;i++){
                if (grid[i][n/2]!=grid[m-1-i][n/2]){
                    par = true;
                    count++;
                }
                else if (grid[i][n/2]==grid[m-1-i][n/2]&&grid[i][n/2]==1){
                    par2++;
                }
            }
        }
        if (par2%2==1&&par == false){
            count+=2;
        }
        return count;
    }
};
