class Solution:
    def minFlips(self, A: List[List[int]]) -> int:
        res = 0
        m, n = len(A), len(A[0])
        total = bian = 0
        for i in range((m + 1) // 2):
            for j in range((n + 1) // 2):
                i2 = m - 1 - i
                j2 = n - 1 - j
                s = set()
                s.add((i,j))
                s.add((i2,j))
                s.add((i,j2))
                s.add((i2,j2))
                v = 0
                for i,j in s:
                    v += A[i][j]
                k = len(s)
                if k == 4:
                    res += min(v, k - v)
                if k == 1:
                    res += v
                # print('here', s, k)
                if k == 2:
                    if v == 1:
                        bian += 1
                    else:
                        total += v
        # print(bian, total)
        if bian == 0 and total % 4:
            res += 2
        return res + bian
