class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        w=len(grid[0])
        h=len(grid)
        dif=0
        sm=0
        fl=0
        for y in range(h//2):
            for x in range(w//2):
                n=grid[y][x]+grid[-y-1][x]+grid[y][-x-1]+grid[-y-1][-x-1]
                dif+=min(n,4-n)
        if h%2==1:
            y=h//2
            for x in range(w//2):
                if grid[y][x]!=grid[y][-x-1]:
                    dif+=1
                    fl+=2
                else:
                    sm+=grid[y][x]*2
        if w%2==1:
            x=w//2
            for y in range(h//2):
                if grid[y][x]!=grid[-y-1][x]:
                    dif+=1
                    fl+=2
                else:
                    sm+=grid[y][x]*2
        if h%2==1 and w%2==1:
            y=h//2
            x=w//2
            dif+=grid[y][x]

        if sm%4==0:
            return dif
        else:
            return dif + max(0,min(4-sm%4-fl,sm%4-fl))
