class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        int m=grid.size(),n=grid[0].size();
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int mid=grid[i][j]+grid[m-1-i][j]+grid[i][n-1-j]+grid[m-1-i][n-1-j];
                ans+=min(mid,4-mid);
            }
        }
        if((m%2==1)&&(n%2==1)){
            ans+=grid[m/2][n/2];
            int cnt=0,s1=0;
            for(int i=0;i<m/2;i++){
                s1+=grid[i][n/2]|grid[m-1-i][n/2];
                if(grid[i][n/2]!=grid[m-1-i][n/2]) cnt++;
            }
            for(int j=0;j<n/2;j++){
                s1+=grid[m/2][j]|grid[m/2][n-1-j];
                if(grid[m/2][j]!=grid[m/2][n-1-j]) cnt++;
            }
            if(s1%2){
                if(cnt) ans+=cnt;
                else ans+=2;
            }else ans+=cnt;
        }else if((m%2==1)||(n%2==1)){
            int s1=0,cnt=0;
            if(m%2){
                for(int j=0;j<n/2;j++){
                    s1+=grid[m/2][j]|grid[m/2][n-1-j];
                    if(grid[m/2][j]!=grid[m/2][n-1-j]) cnt++;
                }
                if(s1%2){
                    if(cnt) ans+=cnt;
                    else ans+=2;
                }else ans+=cnt;
            }else{
                for(int i=0;i<m/2;i++){
                    s1+=grid[i][n/2]|grid[m-1-i][n/2];
                    if(grid[i][n/2]!=grid[m-1-i][n/2]) cnt++;
                }
                if(s1%2){
                    if(cnt) ans+=cnt;
                    else ans+=2;
                }else ans+=cnt;
            }

        }
        return ans;
    }
};
