class Solution {
    public int minFlips(int[][] grid) {
         int n=grid.length;
        int m=grid[0].length;
        int rcount=0,ccount=0,ans=0, one=0,zero=0;
        boolean flag=false;
        for(int i=0;i<(n+1)/2;i++){
            for(int j=0;j<(m+1)/2;j++){
                 int sum=0;

                 if(i==n-1-i && j==m-1-j){
                    sum=grid[i][j];
                    ans+=sum;
                    one+=sum;
                 }
                 else if(i == n-1-i){
                    sum= grid[i][j]+grid[i][m-1-j];
                    ans+=Math.min(sum,2-sum);
                    if(sum<2){
                        one+=sum;
                    }
                    if(sum==1)
                    flag=true;
                 }else if(j== m-1-j){
                    sum= grid[i][j]+ grid[n-1-i][j];
                    ans+=Math.min(sum,2-sum);
                    if(sum<2){
                        one+=sum;
                    }
                    if(sum==1)
                    flag=true;
                 }
                 else{
                 sum= grid[i][j]+grid[i][m-1-j]+ grid[n-1-i][j]+ grid[n-1-i][m-1-j];
                 ans+=Math.min(sum,4-sum);
                 if(sum<=2){
                        one+=sum;
                    }
                    if(sum>2)
                    zero+=4-sum;
                 }

            }
        }

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                rcount+=grid[i][j];
            }
        }
        rcount=rcount -one +zero;
        if(rcount%4!=0){
            if(flag)
            return ans;
            else
            return ans+2;
        }

        return ans;
    }
}
