class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        int z=0;
        int one=0;
        for(int i=0;i<grid.size()/2;i++)
        {
            int i1=grid.size()-1-i;
            int j=0;
            int j1=grid[0].size()-1;
            while(j<j1)
            {
                int x=0;
                int y=0;
                if(grid[i][j]==1)
                {
                    x++;
                }
                else
                {
                    y++;
                }
                if(grid[i][j1]==1)
                {
                    x++;
                }
                else
                {
                    y++;
                }
                if(grid[i1][j]==1)
                {
                    x++;
                }
                else
                {
                    y++;
                }
                if(grid[i1][j1]==1)
                {
                    x++;
                }
                else
                {
                    y++;
                }
                if(y==x)
                {
                    ans+=2;
                }
                else if(x==1)
                {
                    ans+=1;
                }
                else if(y==1)
                {
                    ans+=1;
                }
                j++;
                j1--;
            }
        }
        int o=0;
        int o1=grid.size()-1;
        int l=0;
        int l1=grid[0].size()-1;
        while(l<l1 && grid.size()%2==1)
        {
            if(grid[grid.size()/2][l]!=grid[grid.size()/2][l1])
            {
                ans++;
                z+=2;
            }
            else if(grid[grid.size()/2][l]==grid[grid.size()/2][l1] && grid[grid.size()/2][l1]==1)
            {
                one+=2;
            }
            l++;
            l1--;
        }
        while(o<o1 && grid[0].size()%2==1)
        {
            if(grid[o][grid[0].size()/2]!=grid[o1][grid[0].size()/2])
            {
                ans++;
                z+=2;
            }
            else if(grid[o][grid[0].size()/2]==grid[o1][grid[0].size()/2] && grid[o1][grid[0].size()/2]==1)
            {
                one+=2;
            }
            o++;
            o1--;
        }
        if(one%4!=0 && z<2)
        {
            ans+=2;
        }
        if(grid.size()%2==1 && grid[0].size()%2==1 && grid[grid.size()/2][grid[0].size()/2]==1)
        {
            ans+=1;
        }
        return ans;
    }
};
