class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int sum = grid[i][j] + grid[i][m-1-j] + grid[n-i-1][j] + grid[n-i-1][m-1-j];
                ans += min(sum , 4- sum);
            }
        }
        if((n&1) && (m&1)){
            int one =0, change =0;
            for(int j =0;j<m/2;j++){
                if(grid[n/2][j] != grid[n/2][m-j-1]){
                    change++;
                }else if(grid[n/2][j]==1){
                    one++;
                }
            }
            for(int i =0;i<n/2;i++){
                if(grid[i][m/2] != grid[n-1-i][m/2]){
                    change++;
                }else if(grid[i][m/2]==1){
                    one++;
                }
            }
            if(grid[n/2][m/2]==1) ans ++;
            one*=2;
            if(one%4==0){
                return ans+ change;
            }else{
                if(change>=1) return ans+ change;
                else if(change==0) return ans+ change+2; 
            }
        }else if((n&1)){
            int one =0, change =0;
            for(int j =0;j<m/2;j++){
                if(grid[n/2][j] != grid[n/2][m-j-1]){
                    change++;
                }else if(grid[n/2][j]==1){
                    one++;
                }
            }
            one*=2;
            if(one%4==0){
                return ans+ change;
            }else{
                if(change>=1) return ans+ change;
                else if(change==0) return ans+ change+2; 
            }

        }else if(m&1) {
            int one =0, change =0;
            for(int i =0;i<n/2;i++){
                if(grid[i][m/2] != grid[n-1-i][m/2]){
                    change++;
                }else if(grid[i][m/2]==1){
                    one++;
                }
            }
            one*=2;
            if(one%4==0){
                return ans+ change;
            }else{
                if(change>=1) return ans+ change;
                else if(change==0) return ans+ change+2; 
            }
        }else{
            return ans;
        }
        return 0;
    }
};
