class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m,n=len(grid),len(grid[0])
        cnt=0
        for i in range(m//2):
            for j in range(n//2):
                s=grid[i][j]+grid[m-1-i][j]+grid[i][n-1-j]+grid[m-1-i][n-1-j]
                if s==2:
                    cnt+=2
                else:
                    cnt+=s%2
        if m%2==1 and n%2==0:
            temp=0
            r=sum(grid[m//2])
            for j in range(n//2):
                if grid[m//2][j]!=grid[m//2][n-1-j]:
                    temp+=1
                    r-=1
            if r%4==2 and temp==0:
                cnt+=2
            else:
                cnt+=temp
        if m%2==0 and n%2==1:
            temp=0
            r=sum(grid[i][n//2] for i in range(m))
            for i in range(m//2):
                if grid[i][n//2]!=grid[m-1-i][n//2]:
                    temp+=1
                    r-=1
            if r%4==2 and temp==0:
                cnt+=2
            else:
                cnt+=temp
        if m%2==1 and n%2==1:
            if grid[m//2][n//2]==1:
                cnt+=1
            r=sum(grid[m//2])
            r+=sum(grid[i][n//2] for i in range(m))
            r-=2*grid[m//2][n//2]
            temp1,temp2=0,0
            for j in range(n//2):
                if grid[m//2][j]!=grid[m//2][n-1-j]:
                    temp1+=1
                    r-=1
            for i in range(m//2):
                if grid[i][n//2]!=grid[m-1-i][n//2]:
                    r-=1
                    temp2+=1
            if r%4==2 and temp1==0 and temp2==0:
                    cnt+=2
            else:
                cnt+=temp1+temp2
        return cnt
