class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size(),n=grid[0].size();
        int res=0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int tmp=grid[i][j]+grid[i][n-1-j]+grid[m-1-i][j]+grid[m-1-i][n-1-j];
                res+=min(tmp,4-tmp);
            }
        }
        // cout<<res<<endl;
        int m_no_need1=0,m_need1=0;
        if(m%2){
            for(int i=0;i<n/2;i++){
                if(grid[m/2][i]!=grid[m/2][n-1-i]){
                    m_need1+=1;
                } else {
                    if(grid[m/2][i]==1) m_no_need1+=1;
                }
            }
            // cout<<no_need1<<" "<<need1<<endl;
            // res+=need1;
            // if(need1==0&&no_need1%2) res+=2;
        }
        // cout<<res<<endl;
        int n_no_need1=0,n_need1=0;
        if(n%2){
            for(int i=0;i<m/2;i++){
                if(grid[i][n/2]!=grid[m-1-i][n/2]){
                    n_need1+=1;
                } else {
                    if(grid[i][n/2]==1) n_no_need1+=1;
                }
            }
            // res+=need1;
            // if(need1==0&&no_need1%2) res+=2;
        }
        if((m_no_need1+n_no_need1)%2==1&&m_need1+n_need1==0) res+=2;
        res+=m_need1+n_need1;
        // cout<<res<<endl;
        if(m%2&&n%2&&grid[m/2][n/2]==1) res++;
        return res;
    }
};
