class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans{0},x{0},y{0},z{0},w{0};
        int n = grid.size(), m = grid[0].size();
        for(int i = 0; i < n/2; i++)
        {
            for(int j = 0; j < m/2; j++)
            {
                z = 0;
                z += grid[i][j];
                z += grid[i][m-1-j];
                z += grid[n-1-i][j];
                z += grid[n-1-i][m-1-j];
                if(z==1)
                {
                    ans++;
                }
                if(z==3)
                {
                    ans++;
                }
                if(z==2)
                {
                    ans+=2;
                }
            }
            if(m%2==1)
            {
                z = 0;
                z += grid[i][m/2];
                z += grid[n-1-i][m/2];
                if(z==1)
                {
                    ans++;
                    x++;
                    y+=2;
                }
                if(z==2)
                {
                    y+=2;
                }

            }
        }
        if(n%2==1)
        {
            for(int j = 0; j < m/2; j++)
            {
                z = 0;
                z += grid[n/2][j];
                z += grid[n/2][m-1-j];
                if(z==1)
                {
                    ans++;
                    x++;
                    y+=2;
                }
                if(z==2)
                {
                    y+=2;
                }
            }
            if(m%2==1)
            {
                w = grid[n/2][m/2];
            }
        }
        ans += w;
        if(y%4==2)
        {
            if(x==0)
            {
                ans+=2;
            }
        }
        return ans;
    }
};
